import React, { useState } from 'react';
import { connect } from 'react-redux';

// Action creators
const addTodo = (todo) => ({
    type: 'ADD_TODO',
    payload: todo,
});

const deleteTodo = (index) => ({
    type: 'DELETE_TODO',
    payload: index,
});

const setNewTodo = (text) => ({
    type: 'SET_NEW_TODO',
    payload: text,
});

function TodoList({ todos, newTodo, dispatch }) {
    function handleSubmit(e) {
        e.preventDefault();
        dispatch(addTodo(newTodo)); // Sử dụng action creator addTodo
        dispatch(setNewTodo('')); // Sử dụng action creator setNewTodo
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    value={newTodo}
                    onChange={(e) => dispatch(setNewTodo(e.target.value))} // Sử dụng action creator setNewTodo
                />
                <button type="submit">Add</button>
            </form>
            <div>
                <h2>List of items</h2>
            </div>
            <ul>
                {todos.map((todo, index) => (
                    <li key={index}>
                        {todo}
                        <button onClick={() => dispatch(deleteTodo(index))}>Delete</button> // Sử dụng action creator deleteTodo
                    </li>
                ))}
            </ul>
        </div>
    );
}

const mapStateToProps = (state) => ({
    todos: state.todos,
    newTodo: state.newTodo,
});

export default connect(mapStateToProps)(TodoList);
